- (void)liveSession:(nullable LFLiveSession *)session liveStateDidChange:(LFLiveState)state {
    NSLog(@"%s", __FUNCTION__);
    switch (state) {
        case LFLiveReady:
            _stateLabel.text = @"";
            break;
        case LFLivePending:
            _stateLabel.text = @"";
            break;
        case LFLiveStart:
            _stateLabel.text = @"";
            break;
        case LFLiveError:
            _stateLabel.text = @"";
            break;
        case LFLiveStop:
            _stateLabel.text = @"";
            break;
        default:
            break;
    }
}

